import React, { useState, useMemo } from 'react';
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
  ResponsiveContainer, BarChart, Bar, Area, ComposedChart, AreaChart
} from 'recharts';

const RiskGraph = ({ data = [] }) => {
  const [chartType, setChartType] = useState('area'); // Changed default to Area for better visuals

  // Memoize summary stats so they don't recalculate on every render
  const summary = useMemo(() => {
    if (!data.length) return { avg: 0, peak: 0, alerts: 0, highRisk: 0 };
    return {
      avg: (data.reduce((sum, item) => sum + item.riskScore, 0) / data.length * 100).toFixed(1),
      peak: (Math.max(...data.map(item => item.riskScore)) * 100).toFixed(1),
      alerts: data.reduce((sum, item) => sum + item.alerts, 0),
      highRisk: data.filter(item => item.riskScore > 0.7).length
    };
  }, [data]);

  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div className="custom-tooltip" style={{ backgroundColor: '#1e293b', padding: '10px', border: '1px solid #334155', borderRadius: '4px' }}>
          <p className="tooltip-date" style={{ color: '#94a3b8', marginBottom: '5px' }}>{`Date: ${label}`}</p>
          {payload.map((entry, index) => (
            <div key={index} style={{ display: 'flex', justifyContent: 'space-between', gap: '10px', color: entry.color, fontSize: '0.9rem' }}>
              <span>{entry.name}:</span>
              <span style={{ fontWeight: 'bold' }}>
                {entry.dataKey === 'riskScore' ? entry.value.toFixed(2) : entry.value}
              </span>
            </div>
          ))}
        </div>
      );
    }
    return null;
  };

  return (
    <div className="risk-graph card">
      <div className="graph-header" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
        <h3>Risk Analysis Over Time</h3>
        <div className="chart-controls">
          {['area', 'bar'].map(type => (
            <button
              key={type}
              style={{
                padding: '5px 10px',
                marginLeft: '5px',
                background: chartType === type ? '#3b82f6' : '#1e293b',
                border: '1px solid #334155',
                color: 'white',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
              onClick={() => setChartType(type)}
            >
              {type.charAt(0).toUpperCase() + type.slice(1)} Chart
            </button>
          ))}
        </div>
      </div>

      <div className="graph-container" style={{ height: '400px' }}>
        <ResponsiveContainer width="100%" height="100%">
          {chartType === 'area' ? (
            <AreaChart data={data}>
              <defs>
                <linearGradient id="colorRisk" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#ef4444" stopOpacity={0.8}/>
                  <stop offset="95%" stopColor="#ef4444" stopOpacity={0}/>
                </linearGradient>
              </defs>
              <CartesianGrid strokeDasharray="3 3" stroke="#334155" />
              <XAxis dataKey="date" stroke="#94a3b8" tick={{ fontSize: 12 }} />
              <YAxis domain={[0, 1]} stroke="#94a3b8" tick={{ fontSize: 12 }} />
              <Tooltip content={<CustomTooltip />} />
              <Legend />
              <Area 
                type="monotone" 
                dataKey="riskScore" 
                stroke="#ef4444" 
                fillOpacity={1} 
                fill="url(#colorRisk)" 
                name="Risk Score" 
              />
            </AreaChart>
          ) : (
            <BarChart data={data}>
              <CartesianGrid strokeDasharray="3 3" stroke="#334155" />
              <XAxis dataKey="date" stroke="#94a3b8" />
              <YAxis stroke="#94a3b8" />
              <Tooltip content={<CustomTooltip />} />
              <Legend />
              <Bar dataKey="analyses" fill="#3b82f6" name="Analyses" radius={[4, 4, 0, 0]} />
              <Bar dataKey="alerts" fill="#f59e0b" name="Alerts" radius={[4, 4, 0, 0]} />
            </BarChart>
          )}
        </ResponsiveContainer>
      </div>

      <div className="graph-summary" style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '10px', marginTop: '20px' }}>
        {[
          { label: 'Avg Risk', value: `${summary.avg}%`, color: summary.avg > 50 ? '#ef4444' : '#22c55e' },
          { label: 'Peak Risk', value: `${summary.peak}%`, color: '#f59e0b' },
          { label: 'Total Alerts', value: summary.alerts, color: '#3b82f6' },
          { label: 'High Risk Days', value: summary.highRisk, color: '#ef4444' }
        ].map((item, idx) => (
          <div key={idx} className="summary-item" style={{ background: '#0f172a', padding: '10px', borderRadius: '6px', textAlign: 'center' }}>
            <div style={{ fontSize: '0.8rem', color: '#94a3b8' }}>{item.label}</div>
            <div style={{ fontSize: '1.2rem', fontWeight: 'bold', color: item.color }}>{item.value}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RiskGraph;
