import React, { useState, useEffect, useCallback } from 'react';
import RiskGraph from './RiskGraph';

const Dashboard = () => {
  const [stats, setStats] = useState({
    total_analyses: 0,
    average_risk_score: 0,
    high_risk_analyses: 0,
    active_alerts: 0
  });
  const [graphData, setGraphData] = useState([]);
  const [recentAlerts, setRecentAlerts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [lastUpdated, setLastUpdated] = useState(null);

  // Simulate fetching all dashboard data
  const fetchDashboardData = useCallback(async () => {
    setLoading(true);
    try {
      // 1. Fetch Stats (Mock)
      // const statsRes = await fetch('/api/fraud/stats');
      // const statsData = await statsRes.json();

      // MOCK DATA FOR DEMO
      const mockStats = {
        total_analyses: 1240,
        average_risk_score: 0.34,
        high_risk_analyses: 12,
        active_alerts: 5
      };
      setStats(mockStats);

      // 2. Generate Graph Data (moved from RiskGraph to here)
      const data = [];
      const now = new Date();
      for (let i = 29; i >= 0; i--) {
        const date = new Date(now);
        date.setDate(date.getDate() - i);
        const baseRisk = 0.3 + Math.sin(i / 5) * 0.2;
        const finalRisk = Math.min(1, Math.max(0, baseRisk + (Math.random() - 0.5) * 0.3));
        data.push({
          date: date.toISOString().split('T')[0],
          riskScore: finalRisk,
          analyses: Math.floor(Math.random() * 20) + 5,
          alerts: Math.floor(finalRisk * 10)
        });
      }
      setGraphData(data);

      // 3. Fetch Alerts (Mock)
      // const alertsRes = await fetch('/api/alerts/recent');
      // ...
      setRecentAlerts([
        { id: 1, type: 'secret_leak', severity: 'critical', message: 'AWS Key found', created_at: Date.now()/1000 - 300 },
        { id: 2, type: 'anomaly', severity: 'medium', message: 'Unusual commit time', created_at: Date.now()/1000 - 3600 }
      ]);

      setLastUpdated(new Date());
    } catch (error) {
      console.error("Failed to fetch dashboard data", error);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchDashboardData();
  }, [fetchDashboardData]);

  const getRiskColor = (score) => {
    if (score >= 0.7) return '#ef4444'; // Red
    if (score >= 0.4) return '#f59e0b'; // Orange
    return '#22c55e'; // Green
  };

  return (
    <div className="dashboard container">
      <div className="dashboard-header header">
        <div>
          <h2 style={{ margin: 0 }}>Security Command Center</h2>
          <small style={{ color: '#94a3b8' }}>
            Last updated: {lastUpdated ? lastUpdated.toLocaleTimeString() : '--'}
          </small>
        </div>
        <button 
          className="btn-primary" 
          onClick={fetchDashboardData} 
          disabled={loading}
          style={{ background: '#3b82f6', color: 'white', border: 'none', padding: '10px 20px', borderRadius: '6px', cursor: 'pointer' }}
        >
          {loading ? 'Refreshing...' : 'üîÑ Refresh Data'}
        </button>
      </div>

      {/* KPI Cards */}
      <div className="stats-grid dashboard-grid">
        <div className="card stat-card">
          <div style={{ fontSize: '2rem' }}>üìä</div>
          <div>
            <h3>{stats.total_analyses.toLocaleString()}</h3>
            <p style={{ color: '#94a3b8', margin: 0 }}>Total Scans</p>
          </div>
        </div>

        <div className="card stat-card">
          <div style={{ fontSize: '2rem' }}>üõ°Ô∏è</div>
          <div>
            <h3 style={{ color: getRiskColor(stats.average_risk_score) }}>
              {(stats.average_risk_score * 100).toFixed(1)}%
            </h3>
            <p style={{ color: '#94a3b8', margin: 0 }}>Avg Risk Score</p>
          </div>
        </div>

        <div className="card stat-card">
          <div style={{ fontSize: '2rem' }}>üö®</div>
          <div>
            <h3 style={{ color: stats.high_risk_analyses > 0 ? '#ef4444' : '#22c55e' }}>
              {stats.high_risk_analyses}
            </h3>
            <p style={{ color: '#94a3b8', margin: 0 }}>High Risk Events</p>
          </div>
        </div>

        <div className="card stat-card">
          <div style={{ fontSize: '2rem' }}>üîî</div>
          <div>
            <h3 style={{ color: stats.active_alerts > 0 ? '#f59e0b' : '#94a3b8' }}>
              {stats.active_alerts}
            </h3>
            <p style={{ color: '#94a3b8', margin: 0 }}>Active Alerts</p>
          </div>
        </div>
      </div>

      <div className="dashboard-content dashboard-grid" style={{ marginTop: '20px' }}>
        <div className="chart-section" style={{ gridColumn: 'span 2' }}>
          <RiskGraph data={graphData} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
